package ${packageName}.rest;

import java.util.List;

import javax.validation.Valid;
import javax.ws.rs.Consumes;
import javax.ws.rs.DELETE;
import javax.ws.rs.GET;
import javax.ws.rs.POST;
import javax.ws.rs.PUT;
import javax.ws.rs.Path;
import javax.ws.rs.PathParam;
import javax.ws.rs.Produces;
import javax.ws.rs.WebApplicationException;
import javax.ws.rs.core.MediaType;
import javax.ws.rs.core.Response.Status;

import ${packageName}.beans.${entityName}Bean;
import ${packageName}.dao.${entityName}DAO;
import ${packageName}.dao.exceptions.Conflict;
import ${packageName}.dao.exceptions.NotFound;

@Path("/${entityUrl}s")
public class ${entityName}Service {
	@POST
	@Produces(MediaType.APPLICATION_JSON)
	@Consumes(MediaType.APPLICATION_JSON)
	public ${entityName}Bean create${entityName}(@Valid ${entityName}Bean bean) {
		${entityName}DAO dao = new ${entityName}DAO();
		try {
			dao.create${entityName}(bean);
		} catch (Conflict exc) {
			throw new WebApplicationException(Status.CONFLICT);
		}
		return bean;
	}

	@GET
	@Produces(MediaType.APPLICATION_JSON)
	public List<${entityName}Bean> getAll${entityName}s() {
		${entityName}DAO dao = new ${entityName}DAO();
		return dao.getAll${entityName}s();
	}


	@GET
	@Produces(MediaType.APPLICATION_JSON)
	@Path("/{uuid}")
	public ${entityName}Bean get${entityName}(@PathParam("uuid") String uuid) {
		${entityName}DAO dao = new ${entityName}DAO();

		try {
			return dao.get${entityName}(uuid);
		} catch (NotFound e) {
			throw new WebApplicationException(Status.NOT_FOUND);
		}
	}

	@DELETE
	@Path("/{uuid}")
	public void delete${entityName}(@PathParam("uuid") String uuid) {
		${entityName}DAO dao = new ${entityName}DAO();
		dao.delete${entityName}(uuid);
	}

	@PUT
	@Path("/{uuid}")
	@Produces(MediaType.APPLICATION_JSON)
	@Consumes(MediaType.APPLICATION_JSON)
	public ${entityName}Bean update${entityName}(@PathParam("uuid") String uuid, 
			${entityName}Bean bean) {
		${entityName}DAO dao = new ${entityName}DAO();

		bean.setUuid(uuid);

		try {
			dao.update${entityName}(bean);
		} catch (Conflict c) {
			throw new WebApplicationException(Status.CONFLICT);
		}

		return bean;
	}
}
